import {InferActionsTypes} from "./redux-store";


const initialState = {
    dialogs: [
        {
            id: 1,
            name: 'Andriana',
            avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRpyQ3Ez7fGNDmuULcJxaGc3CxZ5ohwAoFeGQ&usqp=CAU'
        },
        {
            id: 2,
            name: 'Kostiantyn',
            avatar: 'data:image/jpeg;base64,'
        },
        {
            id: 3,
            name: 'Taras',
            avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR8EVwseTPYN3j-9wlUei9zihtwEKu5ozQt1Q&usqp=CAU'
        },
        {
            id: 4,
            name: 'Larisa',
            avatar: 'data:image/jpeg;base64,'
        },
        {
            id: 5,
            name: 'Mikhailo',
            avatar: 'data:image/jpeg;base64,'
        },
        {
            id: 6,
            name: 'Vasylyna',
            avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRSljmIAENV5PNzvZxA9z3y5t_V1AiIVhGzWw&usqp=CAU'
        }
    ] as Array<DialogType>,
    messages: [
        {id: 1, message: 'Hi!'},
        {id: 2, message: 'How are you?'},
        {id: 3, message: 'Yo'},
        {id: 4, message: 'Yo'},
        {id: 5, message: 'Yo'},
    ] as Array<MessageType>
}

const dialogsReducer = (state = initialState, action: ActionsTypes): initialStateType => {
    switch (action.type) {
        case 'dialogs/SEND-MESSAGE':
            const body = action.newMessageBody;
            return {
                ...state,
                messages: [...state.messages, {id: 6, message: body}],
            }
        default:
            return state;
    }
}

export const actions = {
    sendMessageAC: (newMessageBody: string) => ({type: 'dialogs/SEND-MESSAGE', newMessageBody} as const)
}

export default dialogsReducer;


export type DialogType = {
    id: number,
    name: string,
    avatar: string
}
export type MessageType = {
    id: number,
    message: string
}
export type initialStateType = typeof initialState
type ActionsTypes = InferActionsTypes<typeof actions>